import mongoose, { Document, Schema } from "mongoose";
import { text } from "stream/consumers";

export interface IBook extends Document {
  title: String;
  description: String;
  orderId: number;
  price: number;
  available: number;
  datePublished: Date;
}

const bookShema = new Schema({
  title: {
    type: String,
    required: true,
  },
  description: {
    type: String,
    required: true,
  },
  orderId: {
    type: Number,
    required: true,
  },
  price: {
    type: Number,
    required: true,
  },
  available: {
    type: Number,
    required: true,
  },
  datePublished: {
    type: Date,
    required: true,
  },
});

 bookShema.index( {datePublished: -1} );

const Book = mongoose.model<IBook>("Book", bookShema);

export default Book;

import { MongoMemoryServer } from 'mongodb-memory-server';
import mongoose, { ConnectOptions } from 'mongoose';

const mongoServer = new MongoMemoryServer();

const connectionPromise = new Promise((resolve, reject) => {
  mongoServer.start()
    .then(() => {
      const mongoUri = mongoServer.getUri();
      const mongooseOpts = {
        useNewUrlParser: true,
        useUnifiedTopology: true,
        socketTimeoutMS: 30000,
      };
      mongoose.connect(mongoUri, mongooseOpts as ConnectOptions);

      mongoose.connection.on('connected', () => {
        resolve(mongoose.connection);
      });

      mongoose.connection.on('error', (err) => {
        console.error('Mockgoose error');
        console.error(err);

        if (err.message.code === 'ETIMEDOUT') {
          mongoose.connect(mongoUri, mongooseOpts);
        }
      });

      return null;
    })
    .catch((err) => {
      console.error('Error in prepareStorage');
      console.error(err);
      reject(err);
    });
});

/**
 * This promise is resolved when the test database
 * is ready and the connection has been made.
 */
export default connectionPromise;

import { BookFactory } from "../mapper/bookFactory";
import { BookDto } from "../controllers/dto/bookDto";
import { OrderIdsDto } from "../controllers/dto/orderIdsDto";
import { CountBookDto } from "../controllers/dto/countBooksDto";
import Book from "../models/book";
import { BookQueryDto } from "../controllers/dto/bookQueryDto";
import { BookDetailsDto } from "../controllers/dto/bookDetailsDto";
import { BookDtoMapper } from "../mapper/bookDtoMapper";
import { featchOrder } from "../client/httpClient";
import { OrderNotFoundError } from "../../shared/exceptions/orderNotFoundError";

export const addBook = async (bookDto: BookDto): Promise<string> => {
  await validateOrder(bookDto.orderId);
  const book = await BookFactory.createNewBook(bookDto).save();
  return book.id;
};

export const countBooksForOrderIds = async (
  query: OrderIdsDto
): Promise<CountBookDto[]> => {
  const { orderIds } = query;
  const countBooks = await Book.aggregate([
    {
      $match: {
        orderId: { $in: orderIds },
      },
    },
    {
      $group: {
        _id: "$orderId",
        totalBooks: { $sum: 1 },
      },
    },
  ]);

  return countBooks.map((countBook) => BookDtoMapper.mapToCountBook(countBook));
};

export const findBooksByOrderId = async (
  query: BookQueryDto
): Promise<BookDetailsDto[]> => {
  const { orderId, size, from } = query;

  const books = await Book.find({
    ...(orderId && { orderId }),
  })
    .sort({ datePublished: -1 })
    .skip(from)
    .limit(size);

  return books.map((book) => BookDtoMapper.mapToBookDetails(book));
};

const validateOrder = async (orderId?: number) => {
  const res = await featchOrder(orderId);
  if (!res.ok) {
    throw new OrderNotFoundError(orderId);
  }
};

import Book, { IBook } from "../models/book";
import { BookDto } from "../controllers/dto/bookDto";

export class BookFactory {
  static createNewBook = (book: BookDto): IBook => {
    return new Book({
      ...book,
    });
  };
}

import { CountBookDto } from "../controllers/dto/countBooksDto";
import { BookDetailsDto } from "../controllers/dto/bookDetailsDto";
import { IBook } from "../models/book";

export class BookDtoMapper {
  static mapToBookDetails = (book: IBook): BookDetailsDto => {
    return {
      id: book.id,
      orderId: book.orderId,
      price: book.price,
      available: book.available,
      datePublished: book.datePublished,
    };
  };

  static mapToCountBook = (countBooks: any): CountBookDto => {
    return {
      orderId: countBooks._id,
      totalBooks: countBooks.totalBooks,
    };
  };
}

import { HttpClientTimeoutError } from "../../shared/exceptions/httpClientTimoutError";
import { HttpClientError } from "../../shared/exceptions/httpClientError";
import { httpClient } from "../../shared/config";
import log4js from "log4js";

const TIMEOUT_ERROR = "TimeoutError";
const TIMEOUT_DURATION = 2000; //2s

export const featchOrder = async (orderId?: number): Promise<Response> => {
  return await fetch(`${httpClient.url_api}/${orderId}`, {
    signal: AbortSignal.timeout(TIMEOUT_DURATION),
    method: "GET",
    headers: {
      "content-type": "application/json;charset=UTF-8",
    },
  })
    .then((result) => result)
    .catch((err) => {
      if (err.name === TIMEOUT_ERROR) {
        log4js.getLogger().error("Timeout occurred while connecting...", err);
        throw new HttpClientTimeoutError(err);
      }
      log4js.getLogger().error("Error occurred while connecting...", err);
      throw new HttpClientError(err);
    });
};

export class HttpClientTimeoutError extends Error {
    constructor(message: string) {
        super(message)
    }
}

export class HttpClientError extends Error {
    constructor(message: string) {
        super(message)
    }
}

export const httpClient = {
    url_api: "http://localhost:8080/api/orders",  
}

export const config = {
    log4js: {
        appenders: {
            console: {
                type: 'console',
            },
            ms: {
                type: 'dateFile',
                pattern: '-yyyy-MM-dd.log',
                alwaysIncludePattern: true,
                filename: 'log/ms',
                maxLogSize: 1000000,
                compress: true,
            },
        },
        categories: {
            default: {
                appenders: ['ms', 'console'],
                level: 'debug',
            },
        },
    }
}

export class OrderNotFoundError extends Error {
    constructor(orderId?: number) {
        super(`Order with ID: ${orderId} not found!`)
    }
}

export class BookQueryDto {
  orderId?: number;
  from: number;
  size: number;

  constructor(query: Partial<BookQueryDto>) {
    this.orderId = query.orderId;
    this.from = query.from || 0;
    this.size = query.size || 10;
  }
}

export class OrderIdsDto {
    orderIds?: number[];
  
    constructor(data: Partial<OrderIdsDto>) {
      this.orderIds = data.orderIds;
    }
  }
